function markdownToHtml(md, imgMap) {
  let html = md;

  // ì´ë¯¸ì§€ ë³€í™˜: ![alt](images/filename.png) â†’ <img src="data:image/png;base64,...">
  html = html.replace(/!\[([^\]]*)\]\(images\/([^\)]+)\)/g, (match, alt, filename) => {
    if (imgMap[filename]) {
      const imgData = imgMap[filename];
      return `<img src="data:image/${imgData.type};base64,${imgData.base64}" alt="${alt}" style="max-width: 100%; height: auto; margin: 20px 0;" />`;
    }
    // ì´ë¯¸ì§€ ë°ì´í„° ì—†ìœ¼ë©´ í…ìŠ¤íŠ¸ë¡œ
    return `<p><em>[ì´ë¯¸ì§€: ${alt}]</em></p>`;
  });

  // ì œëª© ë³€í™˜
  html = html.replace(/^#### (.+)$/gm, '<h4 style="color: #333; margin-top: 20px;">$1</h4>');
  html = html.replace(/^### (.+)$/gm, '<h3 style="color: #2c3e50; margin-top: 25px;">$1</h3>');
  html = html.replace(/^## (.+)$/gm, '<h2 style="color: #1a5490; margin-top: 30px; border-bottom: 2px solid #1a5490; padding-bottom: 5px;">$1</h2>');
  html = html.replace(/^# (.+)$/gm, '<h1 style="color: #0066cc; margin-top: 30px;">$1</h1>');

  // ë³¼ë“œ/ì´íƒ¤ë¦­
  html = html.replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>');
  html = html.replace(/\*(.+?)\*/g, '<em>$1</em>');

  // ì½”ë“œ
  html = html.replace(/`([^`]+)`/g, '<code style="background: #f4f4f4; padding: 2px 6px; border-radius: 3px; font-family: monospace;">$1</code>');

  // ë§í¬
  html = html.replace(/\[([^\]]+)\]\(([^\)]+)\)/g, '<a href="$2" style="color: #0066cc; text-decoration: none;">$1</a>');

  // ë¦¬ìŠ¤íŠ¸
  html = html.replace(/^- (.+)$/gm, '<li style="margin: 5px 0;">$1</li>');
  html = html.replace(/(<li[^>]*>.*?<\/li>\n?)+/gs, '<ul style="margin: 10px 0; padding-left: 25px;">$&</ul>');

  // êµ¬ë¶„ì„ 
  html = html.replace(/^---$/gm, '<hr style="border: none; border-top: 1px solid #ddd; margin: 20px 0;" />');

